import React, { useState, useEffect } from "react";

// Login Component
const Login = ({ onLogin }) => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(""); // Clear previous errors
    try {
      const response = await fetch(
        "https://frontend-take-home-service.fetch.com/auth/login",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ name, email }),
          credentials: "include",
        }
      );
      if (response.ok) {
        onLogin();
      } else {
        setError("Invalid credentials. Please try again.");
      }
    } catch (error) {
      setError("An error occurred. Please try again later.");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        required
      />
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
      />
      <button type="submit">Login</button>
      {error && <p style={{ color: "red" }}>{error}</p>}
    </form>
  );
};

// Dog Search Component
const DogSearch = () => {
  const [breeds, setBreeds] = useState([]);
  const [dogs, setDogs] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [filters, setFilters] = useState({
    breed: "",
    ageMin: "",
    ageMax: "",
    zipCodes: "",
  });
  const [sort, setSort] = useState("breed:asc");
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const pageSize = 10; // Number of dogs per page

  useEffect(() => {
    fetch("https://frontend-take-home-service.fetch.com/dogs/breeds", {
      credentials: "include",
    })
      .then((response) => response.json())
      .then((data) => setBreeds(data))
      .catch((error) => console.error("Error fetching breeds:", error));
  }, []);

  const handleSearch = async (page = 1) => {
    try {
      const formattedFilters = {
        ...filters,
        breeds: filters.breed ? [filters.breed] : [],
        zipCodes: filters.zipCodes
        ? filters.zipCodes.split(",").filter((zip) => zip.trim() !== "")
        : [],
      };

      const cleanedFilters = Object.fromEntries(
        Object.entries(formattedFilters).filter(([_, value]) => value.length > 0)
      );

      const queryParams = new URLSearchParams({
        ...cleanedFilters,
        sort,
        size: pageSize, // Number of results per page
        from: (page - 1) * pageSize, // Offset
      });

      if (cleanedFilters.zipCodes) {
        cleanedFilters.zipCodes.forEach((zip) =>
          queryParams.append("zipCodes", zip)
        );
      }

      const response = await fetch(
        `https://frontend-take-home-service.fetch.com/dogs/search?${queryParams}`,
        { credentials: "include" }
      );
      const data = await response.json();
      const dogIds = data.resultIds;

      setTotalPages(Math.ceil(data.total / pageSize)); // Set total pages

      const dogDetailsResponse = await fetch(
        "https://frontend-take-home-service.fetch.com/dogs",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(dogIds),
          credentials: "include",
        }
      );
      const dogDetails = await dogDetailsResponse.json();
      setDogs(dogDetails);
      setCurrentPage(page); // Update current page
    } catch (error) {
      console.error("Error searching dogs:", error);
    }
  };

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      handleSearch(currentPage + 1);
    }
  };

  const handlePrevPage = () => {
    if (currentPage > 1) {
      handleSearch(currentPage - 1);
    }
  };

  const toggleSortOrder = () => {
    setSort((prevSort) => (prevSort === "breed:asc" ? "breed:desc" : "breed:asc"));
    handleSearch(1); // Re-fetch results with new sort order
  };

  return (
    <div>
      <h1>Search Dogs</h1>
      <div>
        <select
          value={filters.breed}
          onChange={(e) => setFilters({ ...filters, breed: e.target.value })}
        >
          <option value="">Select Breed</option>
          {breeds.map((breed) => (
            <option key={breed} value={breed}>
              {breed}
            </option>
          ))}
        </select>
        <input
          type="number"
          placeholder="Min Age"
          value={filters.ageMin}
          onChange={(e) => setFilters({ ...filters, ageMin: e.target.value })}
        />
        <input
          type="number"
          placeholder="Max Age"
          value={filters.ageMax}
          onChange={(e) => setFilters({ ...filters, ageMax: e.target.value })}
        />
        <input
          type="text"
          placeholder="Zip Codes"
          value={filters.zipCodes}
          onChange={(e) => setFilters({ ...filters, zipCodes: e.target.value })}
        />
        <button onClick={() => handleSearch(1)}>Search</button>
        <button onClick={toggleSortOrder}>
          Sort by Breed ({sort === "breed:asc" ? "Ascending" : "Descending"})
        </button>
      </div>

      <h2>Results</h2>
      {dogs.map((dog) => (
        <div key={dog.id}>
          <img src={dog.img} alt={dog.name} />
          <p>Name: {dog.name}</p>
          <p>Breed: {dog.breed}</p>
          <p>Age: {dog.age} years old</p>
          <p>Zip Code: {dog.zip_code}</p>
          <button onClick={() => setFavorites((prev) =>
            prev.includes(dog.id) ? prev.filter((id) => id !== dog.id) : [...prev, dog.id]
          )}>
            {favorites.includes(dog.id) ? "Unfavorite" : "Favorite"}
          </button>
        </div>
      ))}

      {/* Pagination Controls */}
      <div>
        <button onClick={handlePrevPage} disabled={currentPage === 1}>
          Previous
        </button>
        <span>
          Page {currentPage} of {totalPages}
        </span>
        <button onClick={handleNextPage} disabled={currentPage === totalPages}>
          Next
        </button>
      </div>
          {/* Generate Match Button */}
    <button onClick={handleMatch}>Generate Match</button>
    {/* Display Matched Dog */}
    {matchedDog && (
      <div>
        <h2>Your Match</h2>
        <img src={matchedDog.img} alt={matchedDog.name} width="100" />
        <p>{matchedDog.name}</p>
        <p>{matchedDog.breed}</p>
        <p>{matchedDog.age} years old</p>
      </div>
    )}
    </div>

  );
};

// App Component
const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  return (
    <div>
      {!isLoggedIn ? (
        <Login onLogin={() => setIsLoggedIn(true)} />
      ) : (
        <DogSearch />
      )}
    </div>
  );
};

export default App;
