import React, { useState, useEffect } from "react";

// Login Component
const Login = ({ onLogin }) => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(""); // Clear previous errors
    try {
      const response = await fetch(
        "https://frontend-take-home-service.fetch.com/auth/login",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ name, email }),
          credentials: "include",
        }
      );
      if (response.ok) {
        onLogin();
      } else {
        setError("Invalid credentials. Please try again.");
      }
    } catch (error) {
      setError("An error occurred. Please try again later.");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        required
      />
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
      />
      <button type="submit">Login</button>
      {error && <p style={{ color: "red" }}>{error}</p>}
    </form>
  );
};

// Dog Search Component
const DogSearch = () => {
  const [breeds, setBreeds] = useState([]);
  const [dogs, setDogs] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [filters, setFilters] = useState({
    breed: "",
    ageMin: "",
    ageMax: "",
    zipCodes: "",
  });
  const [sort, setSort] = useState("breed:asc");
  const [page, setPage] = useState(0); // Current page
  const [totalResults, setTotalResults] = useState(0); // Total number of results
  const pageSize = 25; // Number of results per page

  useEffect(() => {
    // Fetch all breeds
    fetch("https://frontend-take-home-service.fetch.com/dogs/breeds", {
      credentials: "include",
    })
      .then((response) => response.json())
      .then((data) => setBreeds(data))
      .catch((error) => console.error("Error fetching breeds:", error));
  }, []);

  const handleSearch = async () => {
    try {
      // Format filters for the API
      const formattedFilters = {
        ...filters,
        breeds: filters.breed ? [filters.breed] : [], // Ensure breeds is an array
        zipCodes: filters.zipCodes ? filters.zipCodes.split(",") : [], // Split ZIP codes into an array
      };

      // Remove empty filters
      const cleanedFilters = Object.fromEntries(
        Object.entries(formattedFilters).filter(([_, value]) => value.length > 0)
      );

      // Add sort and pagination to the query
      const queryParams = new URLSearchParams({
        ...cleanedFilters,
        sort,
        size: pageSize.toString(), // Number of results per page
        from: (page * pageSize).toString(), // Offset for pagination
      });

      const response = await fetch(
        `https://frontend-take-home-service.fetch.com/dogs/search?${queryParams}`,
        {
          credentials: "include",
        }
      );
      const data = await response.json();
      const dogIds = data.resultIds;
      setTotalResults(data.total); // Store total results for pagination

      // Fetch details of the dogs
      const dogDetailsResponse = await fetch(
        "https://frontend-take-home-service.fetch.com/dogs",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(dogIds),
          credentials: "include",
        }
      );
      const dogDetails = await dogDetailsResponse.json();
      setDogs(dogDetails);
    } catch (error) {
      console.error("Error searching dogs:", error);
    }
  };

  const handleFavorite = (dogId) => {
    setFavorites((prev) =>
      prev.includes(dogId) ? prev.filter((id) => id !== dogId) : [...prev, dogId]
    );
  };

  const [matchedDog, setMatchedDog] = useState(null);

  const handleMatch = async () => {
    if (favorites.length === 0) {
      alert("Please add at least one dog to your favorites before generating a match.");
      return;
    }

    try {
      const response = await fetch(
        "https://frontend-take-home-service.fetch.com/dogs/match",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(favorites),
          credentials: "include",
        }
      );
      const data = await response.json();
      const matchedDogId = data.match;

      // Fetch details of the matched dog
      const matchedDogResponse = await fetch(
        "https://frontend-take-home-service.fetch.com/dogs",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify([matchedDogId]),
          credentials: "include",
        }
      );
      const matchedDogDetails = await matchedDogResponse.json();
      setMatchedDog(matchedDogDetails[0]);
    } catch (error) {
      console.error("Error generating match:", error);
    }
  };

  return (
    <div>
      <h1>Search Dogs</h1>

      {/* Filters */}
      <div>
        <select
          value={filters.breed}
          onChange={(e) => setFilters({ ...filters, breed: e.target.value })}
        >
          <option value="">Select Breed</option>
          {breeds.map((breed) => (
            <option key={breed} value={breed}>
              {breed}
            </option>
          ))}
        </select>
        <input
          type="number"
          placeholder="Min Age"
          value={filters.ageMin}
          onChange={(e) => setFilters({ ...filters, ageMin: e.target.value })}
        />
        <input
          type="number"
          placeholder="Max Age"
          value={filters.ageMax}
          onChange={(e) => setFilters({ ...filters, ageMax: e.target.value })}
        />
        <input
          type="text"
          placeholder="Zip Codes"
          value={filters.zipCodes}
          onChange={(e) => setFilters({ ...filters, zipCodes: e.target.value })}
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {/* Results */}
      <div>
        <h2>Results</h2>
        {dogs.map((dog) => (
          <div key={dog.id}>
            <img src={dog.img} alt={dog.name} width="100" />
            <p>{dog.name}</p>
            <p>{dog.breed}</p>
            <p>{dog.age} years old</p>
            <button onClick={() => handleFavorite(dog.id)}>
              {favorites.includes(dog.id) ? "Unfavorite" : "Favorite"}
            </button>
          </div>
        ))}
      </div>

      {/* Pagination Controls */}
      <div>
        <button
          onClick={() => setPage((prev) => Math.max(prev - 1, 0))}
          disabled={page === 0}
        >
          Previous
        </button>
        <span>Page {page + 1}</span>
        <button
          onClick={() => setPage((prev) => prev + 1)}
          disabled={(page + 1) * pageSize >= totalResults}
        >
          Next
        </button>
      </div>

      {/* Generate Match Button */}
      <button onClick={handleMatch}>Generate Match</button>

      {/* Display Matched Dog */}
      {matchedDog && (
        <div>
          <h2>Your Match</h2>
          <img src={matchedDog.img} alt={matchedDog.name} width="100" />
          <p>{matchedDog.name}</p>
          <p>{matchedDog.breed}</p>
          <p>{matchedDog.age} years old</p>
        </div>
      )}
    </div>
  );
};

// App Component
const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  return (
    <div>
      {!isLoggedIn ? (
        <Login onLogin={() => setIsLoggedIn(true)} />
      ) : (
        <DogSearch />
      )}
    </div>
  );
};

export default App;
